mapper.select:
select tp.post_id as 'postId', tp.title,tpt.type_name as 'postType',tc.company_name as 'companyName', tc.company_logo as 'companyLogo',tp.job_type as 'jobType',reliability , tcy.city_name as 'cityName', tp.post_time as 'postTime' ,tp.Internship_time as 'internshipTime',entry_time as 'entryTime' from tb_post tp LEFT JOIN tb_company tc on tp.company_id=tc.company_id LEFT JOIN tb_post_type tpt on tp.type_id =tpt.type_id LEFT JOIN tb_city tcy on tcy.city_id=tp.city_id
tp.state=1
and tcy.city_name = #{cityName}
and tp.job_type=#{jobType}
and tpt.type_name = #{postType}
order by tp.post_time desc

mapper.update:
update tb_post
set  reliability = #{reliability,jdbcType=INTEGER}
where post_id = #{postId,jdbcType=INTEGER}

update tb_post set state = -1


        and open_id = #{openId,jdbcType=VARCHAR}


        and post_id = #{postId,jdbcType=INTEGER}
